<script>
    export default {
        name: 'detailProd',
        data () {
            return {
                products: null,
                readyFetch: false,
            }
        },
        mounted () {
            this.$nextTick(() => {
                window.addEventListener("scroll", () => {
                    console.log('scrolling')
                    this.theSection.forEach((element) => {
                        if (element.getBoundingClientRect().top - window.innerHeight < -20) {
                            this.readyFetch = true
                        }
                    });
                });

            })
        },
        watch: {
            readyFetch (val) {
                if (this.readyFetch) {
                    this.fetchData ()
                }
            }
        },
        computed : {
            theSection() {
                return document.querySelectorAll("[data-lazy]");
            },
        },
        methods: {
            test () {
                console.log('test')
            },
            fetchData () {
                setTimeout(async () => {
                    try {
                        this.products = (await this.$axios.$get('/products.json', {
                            headers: {
                                "Access-Control-Allow-Origin": "*",
                            }
                        })).datas
                    } catch (error) {
                        alert(error)
                    }
                }, 3000)
            }
        }
    }
</script>

